import{_ as r,R as t,Q as o,aH as a}from"./chunks/framework.eaSbAno9.js";const p=JSON.parse('{"title":"关于 Pro Design","description":"","frontmatter":{"title":"关于 Pro Design","outline":"deep"},"headers":[],"relativePath":"guide/introduction/what-is.md","filePath":"guide/introduction/what-is.md","lastUpdated":1757408630000}'),i={name:"guide/introduction/what-is.md"};function s(n,e,l,d,g,h){return o(),t("div",null,[...e[0]||(e[0]=[a('<h1 id="关于-pro-design" tabindex="-1">关于 Pro Design <a class="header-anchor" href="#关于-pro-design" aria-label="Permalink to “关于 Pro Design”">​</a></h1><p><a href="https://github.com/pro-design-vue" target="_blank" rel="noreferrer">Pro Design</a> 是一个基于 <a href="https://github.com/vuejs/core" target="_blank" rel="noreferrer">Vue3.0</a>、<a href="https://github.com/vitejs/vite" target="_blank" rel="noreferrer">Vite</a>、 <a href="https://www.typescriptlang.org/" target="_blank" rel="noreferrer">TypeScript</a>、<a href="https://www.antdv.com/" target="_blank" rel="noreferrer">Ant Design Vue</a>、<a href="https://jd-opensource.github.io/micro-app/" target="_blank" rel="noreferrer">Micro App</a> 的企业级管理系统框架和组件库，目标是让中后台开发更简单。包括微前端、二次封装组件、utils、hooks、动态菜单、权限校验、多主题配置、按钮级别权限控制等功能。项目会使用前端较新的技术栈，可以作为项目的启动模板，以帮助你快速搭建企业级中后台产品原型。也可以作为一个示例，用于学习 <code>vue3</code>、<code>vite</code>、<code>ts</code> 等主流技术。该项目会持续跟进最新技术，并将其应用在项目中。</p><p><a href="https://github.com/pro-design-vue/pro-design" target="_blank" rel="noreferrer">Pro Design Vue</a> 组件库已经发布到<a href="https://www.npmjs.com/package/pro-design-vue" target="_blank" rel="noreferrer">Npm</a>，组件库可以无缝对接 Ant Design Vue 项目，但是强烈建议搭配 Pro Design Admin 使用，开箱即用，配置完善。</p><p><a href="https://github.com/pro-design-vue/pro-design-admin" target="_blank" rel="noreferrer">Pro Design Admin</a> 企业级管理系统框架，内置微前端支持，主子应用同构框架，可以独立发布使用，也可以作为子应用接入到其他应用。</p><h2 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to “特点”">​</a></h2><ul><li><strong>最新技术栈</strong>：使用 <code>Vue3</code>、<code>Vite</code>、<code>TypeScript</code> 等前端前沿技术开发。</li><li><strong>国际化</strong>：内置完善的国际化方案，支持多语言切换。</li><li><strong>权限验证</strong>：完善的权限验证方案，按钮级别权限控制。</li><li><strong>多主题</strong>：内置多种主题配置和黑暗模式，满足个性化需求。</li><li><strong>动态菜单</strong>：支持动态菜单，可以根据权限配置显示菜单。</li><li><strong>微前端</strong>：支持微前端，基于 <code>Micro App</code> 主子应用同构框架。</li><li><strong>Mock 数据</strong>：基于 <code>AxiosMockAdapter</code> 的本地高性能 Mock 数据方案。</li><li><strong>组件丰富</strong>：提供了丰富的组件，可以满足大部分的业务需求。</li><li><strong>规范</strong>：代码规范，使用 <code>ESLint</code>、<code>Prettier</code>、<code>Stylelint</code>、<code>Commitlint</code> 等工具保证代码质量。</li><li><strong>工程化</strong>：使用 <code>Pnpm Monorepo</code>、<code>Github Workflow</code>、<code>Changeset</code> 等工具，提高开发效率。</li></ul><h2 id="浏览器支持" tabindex="-1">浏览器支持 <a class="header-anchor" href="#浏览器支持" aria-label="Permalink to “浏览器支持”">​</a></h2><ul><li><p><strong>本地开发</strong>推荐使用<code>Chrome 最新版</code>浏览器，<strong>不支持</strong><code>Chrome 80</code>以下版本。</p></li><li><p><strong>生产环境</strong>支持现代浏览器，不支持 IE。</p></li></ul><div class="vp-table"><table><thead><tr><th><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/edge/edge_48x48.png" alt="IE / Edge" width="24px" height="24px"></a>Edge</th><th><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/firefox/firefox_48x48.png" alt="Firefox" width="24px" height="24px"></a>Firefox</th><th><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/chrome/chrome_48x48.png" alt="Chrome" width="24px" height="24px"></a>Chrome</th><th><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/safari/safari_48x48.png" alt="Safari" width="24px" height="24px"></a>Safari</th><th><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/opera/opera_48x48.png" alt="Opera" width="24px" height="24px"></a>Opera</th></tr></thead><tbody><tr><td>Edge</td><td>last 2 versions</td><td>last 2 versions</td><td>last 2 versions</td><td>last 2 versions</td></tr></tbody></table></div><h2 id="贡献" tabindex="-1">贡献 <a class="header-anchor" href="#贡献" aria-label="Permalink to “贡献”">​</a></h2><ul><li><a href="https://github.com/pro-design-vue" target="_blank" rel="noreferrer">Pro Design</a> 还在持续更新中，本项目欢迎您的参与，共同维护，逐步完善，打造更好的中后台解决方案。</li><li>如果你有兴趣加入我们，可以通过以下方式开始，我们会根据你的活跃度邀请你加入。</li></ul><div class="info custom-block"><p class="custom-block-title">加入我们</p><ul><li>长期提交 <code>PR</code>。</li><li>提供有价值的建议。</li><li>参与讨论，帮助解决 <code>issue</code>。</li><li>共同维护文档。</li></ul></div>',12)])])}const b=r(i,[["render",s]]);export{p as __pageData,b as default};
